package blockchain

import (
	"math/big"
	"testing"

	"github.com/btgsuite/btgd/wire"
	btcutil "github.com/btgsuite/btgutil"
)

// TestCalcNextBits ensures CalcNextBits calculates the expected bits value from values
// in last blocks based on LWMA.
func TestCalcNextBits(t *testing.T) {
	powLimit, _ := new(big.Int).SetString("14134776518227074636666380005943348126619871175004951664972849610340958207", 0)
	config := LwmaConfig{
		enableHeight:        14300,
		testnet:             true,
		regtest:             false,
		powTargetSpacing:    600,
		averagingWindow:     45,
		adjustWeight:        13772,
		minDenominator:      10,
		solveTimeLimitation: false,
		powLimit:            powLimit,
	}

	blockHex := ""
	prevBlocksHex := []string{
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
		"",
	}

	bytes := hexToBytes(blockHex)
	block, _ := btcutil.NewBlockFromBytes(bytes)

	blocks := make([]wire.BlockHeader, len(prevBlocksHex))

	for index, hex := range prevBlocksHex {
		blockBytes := hexToBytes(hex)
		prevBlock, _ := btcutil.NewBlockFromBytes(blockBytes)
		blocks[index] = prevBlock.MsgBlock().Header
	}

	r, _ := CalcNextBits(block.MsgBlock().Header, blocks, config)
	bits := block.MsgBlock().Header.Bits
	if r != bits {
		t.Errorf("TestCalcNextBits test failed: got %v want %d\n",
			r, bits)
		return
	}
}
